window.onload=function(){document.querySelector(".wrapper").style.backgroundImage="url(images/mainbackground.jpg)"};const timerOnPage=function(){const e=document.querySelector(".seconds-counter"),n=document.querySelector(".minutes-counter");n.innerText="08";let t=new Date(0);t.setMinutes(t.getMinutes()+8);let o=480,c=8;const r=setInterval((function(){t.getSeconds()>9?e.innerText=t.getSeconds():e.innerText="0"+t.getSeconds(),t.getMinutes()!=c&&(c=t.getMinutes(),n.innerText="0"+t.getMinutes()),t.setSeconds(t.getSeconds()-1),o-=1,o<0&&clearInterval(r)}),1e3)};timerOnPage();