window.onload=function(){document.querySelector(".wrapper").style.backgroundImage="url(images/mainbackground.jpg)"};const timerOnPage=()=>{const e=document.querySelector(".seconds-counter"),t=document.querySelector(".minutes-counter");t.innerText="08";let n=new Date(0);n.setMinutes(n.getMinutes()+8);let o=480,r=8;const c=setInterval(()=>{n.getSeconds()>9?e.innerText=n.getSeconds():e.innerText="0"+n.getSeconds(),n.getMinutes()!=r&&(r=n.getMinutes(),t.innerText="0"+n.getMinutes()),n.setSeconds(n.getSeconds()-1),o-=1,o<0&&clearInterval(c)},1e3)};timerOnPage();